import React, { useEffect, useState } from "react";
import {Switch, Route, Routes} from "react-router-dom";
import '../App.css';
import NavBar from "./NavBar";
import About from "./About";
import FoodList from "./FoodList";
import NewFoodInput from "./NewFoodInput";
import PicnicBasket from "./PicnicBasket";

/*
App
|-NavBar - (NewFoodInput, FoodList, Home/PicnicBasket)
|-FoodList
|-PicnicBasket
  |-FoodItem.map
    |-(+Drop down showing food story)
|-NewFoodInput
*/

function App() {
  const [foods, setFoods] = useState(false)

  useEffect(()=>{
    fetch("http://localhost:3001/foods")
    .then(resp=>resp.json())
    .then(data=>setFoods(data))
  },[])

  function handleFormSubmit(newFoodObj){
    // console.log(newFoodObj)
    fetch("http://localhost:3001/foods",{
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(newFoodObj)
    })
    .then(resp=>resp.json())
    .then(data=>setFoods([...foods, data]))
  }

  return (
    <div className="App">
      <NavBar />
      <Routes>
        <Route path="/newfood" element={<NewFoodInput onFormSubmit={handleFormSubmit}/>} />

        <Route path="/foodlist" element={<FoodList foods={foods}/>} />

        <Route path="/about" element={<About />} />

        <Route exact path="/" element={<PicnicBasket foods={foods}/>} />

        <Route path="*" element={<h2>404 not found</h2>} />

      </Routes>
    </div>
  );
}

export default App;
