import React, { useState } from "react";

function NewFoodInput(){
    //new food obj to patch to db.json
    const [newFood, setNewFood] = useState("");

    //state variables for controlled inputs
    const [foodName, setFoodName] = useState("");
    const [foodCity, setFoodCity] = useState("");
    const [foodCountry, setFoodCountry] = useState("");
    const [foodImg, setFoodImg] = useState("");
    const [imgAlt, setImgAlt] = useState("");
    const [foodStory, setFoodStory] = useState("");

    return (
        <div>
        <h1> New Food ! </h1>
        <form className="newFoodForm">
            <label> What's this food called? 
                <input type="text" name="name" placeholder="What's this food called?"/>
            </label>
            <label>Where did you eat this food?
                <input type="text" name="origin" placeholder="City?"/>
                <input type="text" name="country" placeholder="Country?"/>
            </label>
            <label>Image URL:
                <input type="text" name="image" placeholder="image url"/>
            </label>
            <label>Image Alt Text
                <input type="text" name="altText" placeholder="short image description"/>
            </label>
            <label> Memory Associated With This Food:
                <textarea type="textArea" name="story" placeholder="tell us a story..." onChange={()=>console.log("ayyy")}/>
            </label>
                <input type="submit" name="submit" value="Add Food!"/>
        </form>
        </div>
    )
}

export default NewFoodInput;
